L = 40;H = 36;l = L/2;alpha = 0.1:0.1:5;h = sinh(alpha) ./ alpha;[~, i] = min(abs(h - L/H));alpha_i = alpha(i);lambda = l / sinh(alpha_i);s = 0:0.1:L;x = H/2 - lambda * asinh((l-s) / lambda);y = sqrt(lambda^2 + (l-s).^2) - sqrt(lambda^2 + l^2);b = -H:0.1:4;plot(x, y, 'Color', [0.8 0.8 0.8], 'LineWidth', 2, 0, b, 'k', 'LineWidth', 4, H, b, 'k', 'LineWidth', 4);xlim([-4 H+4]);ylim([-H 8]);xlabel('x');ylabel('y');saveas(gcf, 'pr.png');